import React from 'react';

import { withFirebase } from '../Firebase';

// react plugin for creating notifications over the dashboard
import NotificationAlert from "react-notification-alert";

// reactstrap components
import {
  DropdownItem,
  Button,
  Card,
  CardBody,
  Row,
  Col
} from "reactstrap";


const signButton = ({ firebase }) => (
  <DropdownItem className="nav-item" onClick={firebase.doSignOut}>Sign out</DropdownItem>
);

class SignOutButton extends React.Component {
  constructor(props,firebase) {
    super(props,firebase);
    this.state = {
    };
  }

  notify = place => {
    var color = Math.floor(Math.random() * 5 + 1);
    var type;
    switch (color) {
      case 1:
        type = "primary";
        break;
      case 2:
        type = "success";
        break;
      case 3:
        type = "danger";
        break;
      case 4:
        type = "warning";
        break;
      case 5:
        type = "info";
        break;
      default:
        break;
    }
    var options = {};
    options = {
      place: place,
      message: (
        <div>
          <div>
            Welcome to <b>Black Dashboard React</b> - a beautiful premium admin
            for every web developer.
          </div>
        </div>
      ),
      type: type,
      icon: "tim-icons icon-bell-55",
      autoDismiss: 7
    };
    this.refs.notificationAlert.notificationAlert(options);
  };
  render(firebase) {
    return (
      <>
        <div className="rna-container">
          <NotificationAlert ref="notificationAlert" />
        </div>

        <signButton/>
                            <Button
                              block
                              color="info"
                              onClick={() => this.notify("tr")}
                            >
                              Top Right
                            </Button>

      </>
    );
  }
}


export default withFirebase(SignOutButton);
