import React, { Component } from "react";
import classNames from "classnames";
// react plugin that creates an input with badges
import TagsInput from "react-tagsinput";
// reactstrap components
import {
  Card,
  CardHeader,
  CardBody,
  CardTitle,
  TabContent,
  TabPane,
  Col,
  Button,
  ButtonGroup,
  Row
} from "reactstrap";

import { withFirebase } from "../../components/Firebase";
import UnderConstruction from "../UnderConstruction";

class ProjectPage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      horizontalTabs: "allprojects"
    };
  }
  changeActiveTab = (e, tabState, tadName) => {
    e.preventDefault();
    this.setState({
      [tabState]: tadName
    });
  };

  render() {
    return (
      <>
        <div className="content projects">
          <Col>
            <Card className="card-plain">
              <CardHeader>
                <ButtonGroup
                  className="btn-group-toggle float-left"
                  data-toggle="buttons"
                >
                  <Button
                    color="primary"
                    id="0"
                    size="sm"
                    tag="label"
                    className={classNames("btn-simple", {
                      active:
                        this.state.horizontalTabs === "allprojects"
                          ? "active"
                          : ""
                    })}
                    onClick={e =>
                      this.changeActiveTab(e, "horizontalTabs", "allprojects")
                    }
                  >
                    <input defaultChecked name="options" type="radio" />
                    <span className="d-none d-sm-block d-md-block d-lg-block d-xl-block">
                      All Projects
                    </span>
                    <span className="d-block d-sm-none">
                      <i className="tim-icons icon-single-02" />
                    </span>
                  </Button>
                  <Button
                    color="primary"
                    id="1"
                    size="sm"
                    tag="label"
                    className={classNames("btn-simple", {
                      active:
                        this.state.horizontalTabs === "webapp" ? "active" : ""
                    })}
                    onClick={e =>
                      this.changeActiveTab(e, "horizontalTabs", "webapp")
                    }
                  >
                    <input name="options" type="radio" />
                    <span className="d-none d-sm-block d-md-block d-lg-block d-xl-block">
                      Web Applications
                    </span>
                    <span className="d-block d-sm-none">
                      <i className="tim-icons icon-gift-2" />
                    </span>
                  </Button>
                  <Button
                    color="primary"
                    id="2"
                    size="sm"
                    tag="label"
                    className={classNames("btn-simple", {
                      active:
                        this.state.horizontalTabs === "websites" ? "active" : ""
                    })}
                    onClick={e =>
                      this.changeActiveTab(e, "horizontalTabs", "websites")
                    }
                  >
                    <input name="options" type="radio" />
                    <span className="d-none d-sm-block d-md-block d-lg-block d-xl-block">
                      Websites
                    </span>
                    <span className="d-block d-sm-none">
                      <i className="tim-icons icon-tap-02" />
                    </span>
                  </Button>
                </ButtonGroup>
              </CardHeader>
              <CardBody>
                <TabContent
                  className="tab-space"
                  activeTab={this.state.horizontalTabs}
                >
                  <TabPane tabId="allprojects">
                    <Projects />
                  </TabPane>
                  <TabPane tabId="webapp">
                    <Projects />
                  </TabPane>
                  <TabPane tabId="websites">
                    <Projects />
                  </TabPane>
                </TabContent>
              </CardBody>
            </Card>
          </Col>
        </div>
      </>
    );
  }
}

class ProjectsBase extends Component {
  constructor(props) {
    super(props);

    this.state = {
      title: "",
      description: "",
      imgUrl: "",
      loading: false,
      projects: [],
      limit: 10
    };
  }

  componentDidMount() {
    this.onListenForProjects();
  }

  onListenForProjects = () => {
    this.setState({ loading: true });

    this.props.firebase
      .projects()
      .orderByChild("createdAt")
      .limitToLast(this.state.limit)
      .on("value", snapshot => {
        const projectObject = snapshot.val();

        if (projectObject) {
          const projectList = Object.keys(projectObject).map(key => ({
            ...projectObject[key],
            uid: key
          }));

          this.setState({
            projects: projectList,
            loading: false
          });
        } else {
          this.setState({ projects: null, loading: false });
        }
      });
  };

  componentWillUnmount() {
    this.props.firebase.projects().off();
  }
  onNextPage = () => {
    this.setState(
      state => ({ limit: state.limit + 5 }),
      this.onListenForProjects
    );
  };

  render() {
    const { projects, loading } = this.state;

    return (
      <div className="content">
        {loading && <div>Loading ...</div>}
        {projects && (
          <ProjectList
            projects={projects.map(project => ({
              ...project
            }))}
          />
        )}
        {!projects && (
          <div>
            <UnderConstruction />
          </div>
        )}
      </div>
    );
  }
}

const ProjectList = ({ projects }) => (
  <Row>
    {projects.map(project => (
      <ProjectItem key={project.uid} project={project} />
    ))}
  </Row>
);

class ProjectItem extends Component {
  constructor(props) {
    super(props);

    this.state = {
      tagsinput: [
        this.props.project.tag1,
        this.props.project.tag2,
        this.props.project.tag3,
        this.props.project.tag4
      ] //GOTTA MAP THOSE TAGS
    };
  }

  handleTagsinput = tagsinput => {
    this.setState({ tagsinput });
  };

  render() {
    const { project } = this.props;

    return (
      <Col md="6" lg="4">
        <Card className="portfolio-card">
          <CardHeader>
            <img alt="" src={project.urlPreview} />
            <h4 className="card-category">{project.category}</h4>
            <CardTitle tag="h3">{project.title}</CardTitle>
          </CardHeader>
          <CardBody>
            <p>{project.description}</p>

            <TagsInput
              onChange={this.handleTagsinput}
              tagProps={{
                className: "react-tagsinput-tag primary"
              }}
              value={this.state.tagsinput}
            />
          </CardBody>
        </Card>
      </Col>
    );
  }
}

const Projects = withFirebase(ProjectsBase);

export default ProjectPage;
