import React from "react";

// reactstrap components
import {
  Card,
  CardHeader,
  CardBody,
  CardTitle,
  CardFooter,
  Row,
  Col,
  Button,
  Modal,
  ModalBody
} from "reactstrap";

// core components
class Start extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      modalNotice: false,
      bg: "assets/img/bg-start.jpg"
    };
  }
  toggleModalNotice = () => {
    this.setState({
      modalNotice: !this.state.modalNotice
    });
  };
  render() {
    return (
      <>
        <div className="content start" style={{ background: 'linear-gradient(180deg,rgba(0,0,0,.5),rgba(0, 205, 215, 0.1))', WebkitBackgroundSize: 'cover', MozBackgroundSize: 'cover', OBackgroundSize: 'cover', backgroundSize: 'cover', minHeight: '100vh' }}>
          <Row className="mt-3">
            <Col className="ml-auto mr-auto mt-0" md="6">
              <Card className="card-plain text-center mt-5 mb-0 ">
                <CardHeader className="">
                  <CardTitle className="p-0" tag="h1">
                    <h1 className="m-0">PIETER LEMS</h1>
                  </CardTitle>
                  <CardBody className="p-0">
                    <img style={{ width: '40px', margin: 'auto' }} alt="..." src={require("assets/img/pflogo.png")} />
                    <img style={{ display: 'none' }} alt="..." src={require("assets/img/pf-bg.jpg")} />
                    <h3 className="mt-3" style={{ fontWeight: '200' }}>Computer Science student at the university of Applied Sciences, Freelance Website & Web application developer</h3>
                  </CardBody>
                  <CardFooter>
                    <div className="button-container">
                      <Button className="btn" color="primary" onClick={this.toggleModalNotice}>
                        <i className="tim-icons icon-badge mb-1" /> &nbsp; Resume
                </Button>
                      <Button className="btn" color="primary">
                        <i className="fab fa-github" /> &nbsp; Github
                </Button>
                    </div>
                  </CardFooter>
                </CardHeader>
              </Card>
            </Col>
          </Row>
        </div>


        {/*Modal*/}
        <Modal
          isOpen={this.state.modalNotice}
          toggle={this.toggleModalNotice}
        >
          <ModalBody className="text-center">
          </ModalBody>
          <div className="modal-footer justify-content-center">
            <button
              aria-hidden={true}
              className="close"
              data-dismiss="modal"
              type="button"
              onClick={this.toggleModalNotice}
            >
              <i className="tim-icons icon-simple-remove" />
            </button>
          </div>
        </Modal>
        <footer style={{ display: 'none' }}></footer>
      </>
    );
  }
}

export default Start;
