import React from "react";
// react plugin used to create a form with multiple steps
import ReactWizard from "react-bootstrap-wizard";

// reactstrap components
import { Col,Form } from "reactstrap";

// wizard steps
import Step1 from "./Step1.jsx";
import Step2 from "./Step2.jsx";
import Step3 from "./Step3.jsx";

var steps = [
  {
    stepName: "About",
    stepIcon: "tim-icons icon-single-02",
    component: Step1
  },
  {
    stepName: "Skills",
    stepIcon: "tim-icons icon-trophy",
    component: Step2
  },
  {
    stepName: "Contact",
    stepIcon: "tim-icons icon-send",
    component: Step3
  }
];

class Wizard extends React.Component {
  render() {
    return (
      <>
        <div className="content about">
        <Form name="gform" id="gform" enctype="text/plain" action="https://docs.google.com/forms/d/e/1FAIpQLSeoU44kzeJddnl45ZnxqGKGLLThCik1Wh4ta6GbiiyvpfWCZw/formResponse?usp=sf_link" target="hidden_iframe" onsubmit="submitted=true;">
          <Col className="mr-auto ml-auto mt-0" md="10">

            <ReactWizard
              steps={steps}
              navSteps
              validate
              title
              description
              headerTextCenter
              finishButtonText="finish"

              finishButtonClasses="btn-wd btn-primary"
              nextButtonClasses="btn-wd btn-primary"
              previousButtonClasses="btn-wd btn-primary"
              progressbar
              color="primary"
            />
          </Col>
          <input type="submit" value="Submit"/>
          </Form>
        </div>
      </>
    );
  }
}

export default Wizard;
