import React, { Component } from "react";
import { compose } from "recompose";
// react plugin that creates an input with badges
import TagsInput from "react-tagsinput";
// reactstrap components
import {
  Card,
  CardHeader,
  CardBody,
  CardTitle,
  TabContent,
  TabPane,
  Row,
  Col,
  Button,
  ButtonGroup
} from "reactstrap";

import {
  AuthUserContext,
  withAuthorization,
  withEmailVerification
} from "../../components/Session";
import { withFirebase } from "../../components/Firebase";

class ProjectPage extends Component {
  constructor(props) {
    super(props);

    this.state = {
      users: null
    };
  }

  componentDidMount() {
    this.props.firebase.users().on("value", snapshot => {
      this.setState({
        users: snapshot.val()
      });
    });
  }

  componentWillUnmount() {
    this.props.firebase.users().off();
  }

  render() {
    return (
      <div className="content">
        <Projects users={this.state.users} />
      </div>
    );
  }
}

class ProjectsBase extends Component {
  constructor(props) {
    super(props);

    this.state = {
      title: "",
      description: "",
      imgUrl: "",
      loading: false,
      projects: [],
      limit: 5
    };
  }

  componentDidMount() {
    this.onListenForProjects();
  }

  onListenForProjects = () => {
    this.setState({ loading: true });

    this.props.firebase
      .projects()
      .orderByChild("createdAt")
      .limitToLast(this.state.limit)
      .on("value", snapshot => {
        const projectObject = snapshot.val();

        if (projectObject) {
          const projectList = Object.keys(projectObject).map(key => ({
            ...projectObject[key],
            uid: key
          }));

          this.setState({
            projects: projectList,
            loading: false
          });
        } else {
          this.setState({ projects: null, loading: false });
        }
      });
  };

  componentWillUnmount() {
    this.props.firebase.projects().off();
  }

  onChangeTitle = event => {
    this.setState({ title: event.target.value });
  };
  onChangeDescription = event => {
    this.setState({ description: event.target.value });
  };

  onCreateProject = (event, authUser) => {
    this.props.firebase.projects().push({
      title: this.state.title,
      description: this.state.description,
      userId: authUser.uid,
      createdAt: this.props.firebase.serverValue.TIMESTAMP
    });

    this.setState({ title: "" });
    this.setState({ description: "" });

    event.preventDefault();
  };

  onEditProject = (project, title, description) => {
    this.props.firebase.project(project.uid).set({
      ...project,
      title,
      description,
      editedAt: this.props.firebase.serverValue.TIMESTAMP
    });
  };

  onRemoveProject = uid => {
    this.props.firebase.project(uid).remove();
  };

  onNextPage = () => {
    this.setState(
      state => ({ limit: state.limit + 5 }),
      this.onListenForProjects
    );
  };

  render() {

    const { title, description, projects, loading } = this.state;

    return (
      <div>
            {loading && <div>Loading ...</div>}

            {projects && (
              <ProjectList
                projects={projects.map(project => ({
                  ...project
                }))}
              />
            )}

            {!projects && <div>There are no projects ...</div>}
          </div>
    );
  }
}

const ProjectList = ({ projects}) => (
  <Row>
      {projects.map(project => (
        <ProjectItem
          key={project.uid}
          project={project}
        />
      ))}
      </Row>
);

class ProjectItem extends Component {
  constructor(props) {
    super(props);

    this.state = {
      tagsinput: [], //GOTTA MAP THOSE TAGS
      editMode: false,
      editTitle: this.props.project.title
    };
  }

  handleTagsinput = tagsinput => {
    this.setState({ tagsinput });
  };

  onToggleEditMode = () => {
    this.setState(state => ({
      editMode: !state.editMode,
      editTitle: this.props.project.title
    }));
  };

  onChangeEditTitle = event => {
    this.setState({ editTitle: event.target.value });
  };

  onSaveEditTitle = () => {
    this.props.onEditProject(this.props.project, this.state.editTitle);

    this.setState({ editMode: false });
  };

  render() {
    const { project, onRemoveProject } = this.props;
    const { editMode, editTitle } = this.state;

    return (
      <Col md="6" lg="3">
        <Card className="portfolio-card">
          <CardHeader>
            <img alt="" src="https://i.imgur.com/bSCTSzw.jpg" />
            <h4 className="card-category">Web Application</h4>
            <CardTitle tag="h3">{project.title}</CardTitle>
          </CardHeader>
          <CardBody>
            <p>
              {project.description}
              A Cross-Platform application created with Xamarin and C#. The goal
              of this project is that users can find houses on sale in safe
              neighbourhoods. If the user selects a house he / she can see the
              crime rates ordered by sort.
            </p>

            <TagsInput
              onChange={this.handleTagsinput}
              tagProps={{
                className: "react-tagsinput-tag primary"
              }}
              value={this.state.tagsinput}
            />
          </CardBody>
        </Card>
      </Col>
    );
  }
}

const Projects = withFirebase(ProjectsBase);

const condition = authUser => !!authUser;

export default compose(
  withFirebase,
  withEmailVerification,
  withAuthorization(condition)
)(ProjectPage);
